import React, { useState} from 'react';
import './index.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronRight, faChevronLeft, faCircle, faCheckCircle, faPlus } from '@fortawesome/free-solid-svg-icons';

const App = () => {
  const [items, setItems] = useState([
	{itemName: "items 1", quality: 1, isSelected: false},
	{itemName: "items 2", quality: 2, isSelected: true},
	{itemName: "items 3", quality: 3, isSelected: false},

  ]);

  const [inputValue, setInputValue] = useState("");
  return (
    <div className='app-background'>
			<div className='main-container'>
				<div className='add-item-box'>
					<input value={inputValue} onChange={(event)=> setInputValue(event.target.value)} className='add-item-input' placeholder='Add an item...' />
					<FontAwesomeIcon icon={faPlus} />
				</div>
				<div className='item-list'>
					{items.map((item, index)=> <div className='item-container'>
						<div className='item-name'>
							{/* HINT: replace false with a boolean indicating the item has been completed or not */}
							{item.isSelected ? (
								<>
									<FontAwesomeIcon icon={faCheckCircle} />
									<span className='completed'>{item.itemName}</span>
								</>
							) : (
								<>
									<FontAwesomeIcon icon={faCircle} />
									<span>{item.itemName}</span>
								</>
							)}
						</div>
						<div className='quantity'>
							<button>
								<FontAwesomeIcon icon={faChevronLeft} />
							</button>
							<span> {item.quality} </span>
							<button>
								<FontAwesomeIcon icon={faChevronRight} />
							</button>
						</div>
					</div>)}

				</div>
				<div className='total'>Total: 6</div>
			</div>
		</div>
  );
}

export default App;
